import React, { Component } from "react";
import "./BlogPost.css";
import Post from "../../component/BlogPost/Post";

class BlogPost extends Component {
  //komponen state dari React untuk statefull component
  state = {
    //variabel array yang digunakan untuk menyimpan dat API
    listArtikel: [],
  };

  ambilDataDariServerAPI = () => {
    fetch("  http://localhost:3000/posts")
      //ubah response data dari URL API menjadi sebuah data json
      .then((response) => response.json())
      .then((jsonHasilAmbilDariAPI) => {
        //data json hasil ambil dari API kita masukkan ke dalam listArtikel
        this.setState({
          listArtikel: jsonHasilAmbilDariAPI,
        });
      });
  };

  //komponen untuk mengecek ketika component telah dimounting, maka dipanggil API
  componentDidMount() {
    //alamat URL API yang ingin kita ambil datanya
  }

  render() {
    return (
      <div className="post-artikel">
        <h2>Daftar Artikel</h2>
        {/* //looping dan masukkan untuk setiap data yang ada di listArtikel */}
        {this.state.listArtikel.map((artikel) => {
          //mappingkan data json dari API sesuai denga  kategorinya
          return <Post key={artikel.id} judul={artikel.title} isi={artikel.body} />;
        })}
      </div>
    );
  }
}

export default BlogPost;
